import React from 'react';
import {
  Form, Icon, Input, Button, Checkbox, Alert
} from 'antd';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from './../../store/actions/index';
import { validation } from './validations';

class Login extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      user: '',
      pass: '',
      errors: []
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    const data = {user: this.state.user, pass: this.state.pass}
    const valid = validation(data); 
   if (!valid.valid) {
     this.setState({errors: valid.errors})
     return;
   }
    //this.props.onSetSesion();
  }

  render() {
    if (this.props.redirect) { return (<Redirect to="/vehicles" />) }

    const {errors} = this.state;

    return(
      <>
        <Form onSubmit={this.handleSubmit} className="login-form" id="components-form-demo-normal-login">
          <Form.Item>
            <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="Username" />
          </Form.Item>
          <Form.Item>
            <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="Password" />
          </Form.Item>
          <Form.Item>
            <Checkbox>Remember me</Checkbox>
            <a className="login-form-forgot" href="">Forgot password</a>
            <Button type="primary" htmlType="submit" className="login-form-button">
              Log in
            </Button>
            Or <a href="">register now!</a>
          </Form.Item>
        </Form>

        {errors.map((error, i) => (
          <Alert
            key={key}
            message="Error"
            description={error}
            type="error"
            closable
          />
        ))}
      </>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onSetSesion: () => dispatch(actions.setSesion())
  }
}

const mapStateToProps = state => {
  return {
    redirect: state.auth.redirect
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
